import React, { useContext, useEffect } from 'react'
import { useHistory } from 'react-router';
import { Context } from './Context';

export const ResponseScreen = () => {
    
    const {setcheckpoints, setActiveStep} = useContext (Context);

    useEffect(() => {
        setActiveStep(2);
    }, [])

    const history = useHistory(); 


    const handleReturnHome = () =>{
        setcheckpoints({'acceptContract':false, 'passwordAccepted':false})
        history.push('./');
    }

    return (
        <>
            <h3>
                Exito!!!
            </h3>
            
            <input type="button" value="Volver al inicio" onClick={handleReturnHome}></input>
            
        </>
    )
}
