import React, { useState, useContext, useEffect } from 'react'
import { Link } from 'react-router-dom';
import { Context } from './Context';
import imagen1 from '../assets/img/group.svg'
import imagen2 from '../assets/img/group-3.svg'
export const HomeScreen = () => {
    
    const [accept, setaccept] = useState(true);

    const {checkpoints, setcheckpoints, setActiveStep} = useContext (Context);

    useEffect(() => {
        setActiveStep(0);
    }, [])

    const handleAcceptChange = () =>{
        setaccept (!accept);
        if(checkpoints.acceptContract===false)
        {setcheckpoints({...checkpoints, 'acceptContract':true})}
        else{setcheckpoints({...checkpoints, 'acceptContract':false})};
    }


    return (
        <div className="base__container">
            <div className="base__content">
                <h1>Crea tu Password Manager</h1>
                <div className="base__underline"></div>
                <div className="container-fluid"></div>
                <div className="row">
                    <div className="col-lg-6">
                        <img  alt="headsvg" src={imagen1} className="screen1__imagen"/>
                        <h3>Guarda aquí todas tus contraseñas, datos o cualquier información, olvida las notas de papel y las aplicaciones no protegidas.</h3>
                    </div>
                    <div className="col-lg-6">
                    <img alt="boxsvg" src={imagen2} className="screen1__imagen"/>
                        <h3>Crea tu clave maestra: solo tú podrás acceder a tus secretos con ella.</h3>
                    </div>
                </div>

                <h2>Cómo funciona</h2>
                <h3>En primer lugar, debes crear una contraseña diferente para tus pertenencias electrónicas. no podrás recuperar tu contraseña, así que recuérdela bien.</h3>

                <h2>Qué datos puedes guardar</h2>
                <h3>Por ejemplo, el número de tu tarjeta, el PIN y PUK de tu teléfono móvil, el número de series de alguno de tus dispositivos o cualquier información que necesites tener en un lugar seguro.</h3>

                <form onChange={handleAcceptChange} className="screen1__form">
                    <input className="form-check-input" type="checkbox"/>
                        <p>Soy mayor de edad y he leído y acepto el tratamiento de mis datos según la 
                            <Link to='/terms-and-conditions' className="base__Link">
                                <strong> política de protección de datos</strong>
                            </Link>.
                        </p>
                </form>
                <button className="btn btn-light">Cancelar</button>
                <Link to={'./create-password'}>
                        <button disabled={accept} type="button" className="btn btn-primary">Siguiente <i className="bi bi-chevron-right"></i></button>
                </Link>
            </div>
        </div>        
    )
}
