import React, { useContext, useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom'
import {Formik, Form} from 'formik';
import * as Yup from 'yup';
import { TextField } from './TextField';
import { CheckItems } from './CheckItems';
import { Context } from './Context';
import { submitForm } from '../services/api';
import { Loading } from './Loading';


export const CreatePasswordScreen = () => {

     const history = useHistory();     

    const {checkpoints, setcheckpoints, setActiveStep} = useContext (Context);

    useEffect(() => {
        setActiveStep(1);
    }, [])

    const [isLoading, setisLoading] = useState(true);


    const handleReturnHome = () =>{
      setcheckpoints({...checkpoints, 'acceptContract':false})
      history.push('./');
    }

    const validate = Yup.object({
        password: Yup.string()
      .min(8, 'La contraseña debe poseer al menos 8 caracteres')
      .required('La contraseña es requerida')
      .matches(
        /^.*((?=.*[0-9]){1}).*$/,
        "La contraseña debe poseer al menos un número"
      )
      .matches(
        /^.*((?=.*[A-Z]){1}).*$/,
        "La contraseña debe poseer al menos una mayúscula"
      ).matches(
        /^.*((?=.*[a-z]){1}).*$/,
        "La contraseña debe poseer al menos una minúscula"
      ),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), null], 'Las contraseñas deben coincidir')
      .required('La confirmación de contraseña es requerida'),
    pista: Yup.string()
      .max(255, 'La pista no puede superar los 255 caracteres')
      
    })
 
  return (
  
     <div className="base__container">
      <div className="base__content">
        
        <h1>Crea tu Password Manager</h1>
        <div className="base__underline"></div>
        <h3>
          En primer lugar, debes crear una contraseña diferente para tus pertenencias electrónicas. <br/>
          No podrás recuperar tu contraseña, así que recuérdala bien.
        </h3>
        {isLoading && <Loading />}
        
          <Formik
              initialValues={{
                  password: '',
                  confirmPassword: '',
                  pista: '',
              }}
          
              validationSchema={validate}

              onSubmit={values => {
                setisLoading(true);
                console.log(isLoading)
                submitForm(values.password, values.confirmPassword, values.pista)
                .then(response => console.log(response))
                .catch(function(err) {
                  console.log(err);
                })
                .finally(
                  setisLoading(false),
                  console.log(isLoading));
                
                 
              }}
          >
      
              {formik => (
                  <Form>
                      <CheckItems values={formik.values}/>
                      <TextField label="Crea tu Contraseña Maestra" name="password" type="password"  placeholder="Elige una contraseña"/>
                      <TextField label="Repite tu Contraseña Maestra" name="confirmPassword" type="password" placeholder="Repite tu contraseña"/>
                      <h3>También puedes crear una pista que te ayude a recordar tu contraseña maestra. <i className="bi bi-info-circle screen3__infoicon"></i></h3>
                      <TextField label="Crea tu pista para recordar tu contraseña maestra (opcional)" name="pista" type="text" placeholder="Introduce tu pista"/>
                      <button className="btn btn-primary" type="submit">Siguiente <i className="bi bi-chevron-right"></i></button>
                      <button className="btn btn-light" onClick={handleReturnHome}>Cancelar</button>
                  </Form>
              )}
          </Formik>
      </div>
    </div> 
    )
}
