import React, { useEffect, useState } from 'react'

export const CheckItems = ({values}) => {

    let password = null;
    let confirmPassword = null;

    if(password !==values.password)
    {password = values.password;}        
    
    if(confirmPassword !==values.confirmPassword)
    {confirmPassword = values.confirmPassword;}        

    let capital = new RegExp("^(?=.*[A-Z])");
    let lower = new RegExp("^(?=.*[a-z])");
    let num = new RegExp("^(?=.*[0-9])");
    let len = new RegExp("^(?=.{8,})");
    
    const [capitalletter, setcapitalletter] = useState(false)
    const [lowerletter, setlowerletter] = useState(false)
    const [numbers, setnumbers] = useState(false)
    const [length, setlength] = useState(false)
    const [equal, setequal] = useState(false)

    useEffect(() => {
        if (capital.test(password)) {setcapitalletter(true)} else {setcapitalletter(false)}
        if (lower.test(password)) {setlowerletter(true)} else {setlowerletter(false)}
        if (num.test(password)) {setnumbers(true)} else {setnumbers(false)}
        if (len.test(password)) {setlength(true)} else {setlength(false)} 
        if (password===confirmPassword && password.length>7) {setequal(true)} else {setequal(false)}
    }, [password,confirmPassword])

    return (
        <>
          <h3><strong>Reglas para la creación de la Contraseña Maestra</strong></h3> 
            <span>Mínimo una mayúscula</span> {capitalletter && <i className="bi bi-check-circle screen3__icons"></i>} <br/>
            <span>Mínimo una minúscula</span> {lowerletter && <i className="bi bi-check-circle screen3__icons"></i>} <br/>
            <span>Mínimo un número</span> {numbers && <i className="bi bi-check-circle screen3__icons"></i>} <br/>
            <span>Mínimo 8 caracteres</span> {length && <i className="bi bi-check-circle screen3__icons screen3__icons"></i>} <br/>
            <span>Las contraseñas deben coincidir</span> {equal && <i className="bi bi-check-circle screen3__icons"></i>} <br/> 
        </>
    )
}
