import React, { useContext, useEffect, useState } from 'react'
import { useHistory } from 'react-router';
import { Context } from './Context';
import { ResponseKO } from './ResponseKO';
import { ResponseOK } from './ResponseOK';

export const ResponseScreen = () => {
    
    const {setcheckpoints, setActiveStep, apiresponse, setapiresponse} = useContext (Context);

    const [resultado, setresultado] = useState(null);

    useEffect(() => {
        setActiveStep(2);
    }, [])
    
    useEffect(() => {
        if(apiresponse.status!==200){
            setresultado(false)
        }else{
            setresultado(true); 
            setActiveStep(3);
        }
    }, [])
    
    
    

    const history = useHistory(); 


    const handleReturnHome = () =>{
        setcheckpoints({'acceptContract':false, 'passwordAccepted':false});
        setapiresponse({'status': ""})
        history.push('./');
    }

    return (
        <div className="base__container">
            <div className="base__content">
                <h1>Crea tu Password Manager</h1>
                <div className="base__underline"></div>
            {resultado?<ResponseOK />:<ResponseKO />}   
            
            <input type="button" value="Volver al inicio" onClick={handleReturnHome}></input>
            
            </div> 
        </div>
    )
}
