import React, { useContext, useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom'
import {Formik, Form} from 'formik';
import * as Yup from 'yup';
import { TextField } from './TextField';
import { CheckItems } from './CheckItems';
import { Context } from '../Context';
import { submitForm } from '../../services/api';
import { Loading } from './Loading';


export const CreatePasswordScreen = () => {

    const history = useHistory();     

    const {checkpoints, setcheckpoints, setActiveStep, setapiresponse} = useContext (Context);

    const [isLoading, setisLoading] = useState(false);

    useEffect(() => {
        setActiveStep(1);
    }, [setActiveStep])

    const handleReturnHome = () =>{
      setcheckpoints({...checkpoints, 'acceptContract':false})
      history.push('./');
    }

    const validate = Yup.object({
        password: Yup.string()
      .min(8, 'La contraseña debe poseer al menos 8 caracteres')
      .required('La contraseña es requerida')
      .matches(
        /^.*((?=.*[0-9]){1}).*$/,
        "La contraseña debe poseer al menos un número"
      )
      .matches(
        /^.*((?=.*[A-Z]){1}).*$/,
        "La contraseña debe poseer al menos una mayúscula"
      ).matches(
        /^.*((?=.*[a-z]){1}).*$/,
        "La contraseña debe poseer al menos una minúscula"
      ),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), null], 'Las contraseñas deben coincidir')
      .required('La confirmación de contraseña es requerida'),
    pista: Yup.string()
      .max(255, 'La pista no puede superar los 255 caracteres')
    })

  return (
    <>
    {isLoading && <Loading />}
    <div className="base__container">
      <div className="base__content">
        
        
        <h1>Crea tu Password Manager</h1>
        <div className="base__underline"></div>
        <h3>
          En primer lugar, debes crear una contraseña diferente para tus pertenencias electrónicas. <br/>
          No podrás recuperar tu contraseña, así que recuérdala bien.
        </h3>
        
        
          <Formik
              initialValues={{
                  password: '',
                  confirmPassword: '',
                  pista: '',
              }}
          
              validationSchema={validate}

              onSubmit={values => {
                setisLoading(true);
                submitForm(values.password, values.confirmPassword, values.pista)
                .then(response => setapiresponse(response))
                .catch(function(err) {
                  console.log(err); //si bien el manejo del error es tenido en cuenta ya que en la pantalla de feedback mostrará que no se puedo crear Password Manager hago un console.log del error por control de programación
                })
                .finally(
                  setTimeout(() => {
                    setcheckpoints({...checkpoints, 'passwordAccepted':true});
                    history.push('./response');
                  }, 3020)
                );
              }}
          >
      
              {formik => (
                  <Form>
                      <CheckItems values={formik.values}/>
                      
                      <div className="container">
                        <div className="row">
                          <div className="col-lg-5">
                            <TextField label="Crea tu Contraseña Maestra" name="password" type="password" placeholder="Elige una contraseña"/>
                          </div>
                          <div className="col-lg-5">
                            <TextField label="Repite tu Contraseña Maestra" name="confirmPassword" type="password" placeholder="Repite tu contraseña"/>
                          </div>
                        </div>
                      </div>
                      <div>
                        <h3>También puedes crear una pista que te ayude a recordar tu contraseña maestra. 
                          <button type="button" class="btn btn-light" data-toggle="tooltip" data-placement="top" title="Admite hasta 255 caracteres">
                            <i className="bi bi-info-circle screen3__infoicon"></i>
                          </button>
                        </h3>
                      </div>
                      
                      
                      <TextField label="Crea tu pista para recordar tu contraseña maestra (opcional)" maxLength="255" name="pista" type="text" placeholder="Introduce tu pista"/>
                      <div className="screen2__contadorbox"><h3 className="screen2__contador">{formik.values.pista.length}/255</h3></div>
                      
                      
                      <button className="btn btn-primary" type="submit">Siguiente <i className="bi bi-chevron-right"></i></button>
                      <button className="btn btn-light" type="reset" onClick={handleReturnHome}>Cancelar</button>
                  </Form>
              )}
          </Formik>
      </div>
    </div> 
    </>
    )
}
